import { useEffect, useState, useRef } from "react";
import { X } from "lucide-react";

// Define the props type
interface ChatNotificationProps {
  title: string;
  message: string;
  onClose: () => void;
}

export function ChatNotification({ title, message, onClose }: ChatNotificationProps) {
  const [audioSrc, setAudioSrc] = useState<string | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  // Fallback URL
  const fallbackAudioUrl = "https://rimxuidev.vercel.app/audio/NotificationSound.mp3"; // Replace with your actual remote URL

  useEffect(() => {
    const checkLocalAudio = async () => {
      try {
        // Check if the local file exists
        const response = await fetch("/NotificationSound.mp3", { method: "HEAD" });

        if (response.ok) {
          // If the file exists locally, use the local path
          setAudioSrc("/NotificationSound.mp3");
        } else {
          // Fallback to remote URL if local file doesn't exist
          setAudioSrc(fallbackAudioUrl);
        }
      } catch (error) {
        console.warn("Error fetching the local audio file:", error);
        // Fallback to remote URL in case of network error
        setAudioSrc(fallbackAudioUrl);
      }
    };

    checkLocalAudio();
  }, []);

  useEffect(() => {
    if (audioRef.current && audioSrc) {
      audioRef.current.play().catch((e) => {
        console.warn("Sound couldn't be played automatically:", e);
      });
    }
  }, [audioSrc]);

  return (
    <div className="max-w-sm w-full bg-white dark:bg-gray-900 shadow-lg rounded-lg p-4 flex justify-between items-start gap-3 border border-gray-200 dark:border-gray-700">
      {audioSrc && <audio ref={audioRef} src={audioSrc} preload="auto" />}
      <div className="flex-1">
        <h4 className="font-semibold text-gray-900 dark:text-white text-base mb-1">
          {title}
        </h4>
        <p className="text-sm text-gray-700 dark:text-white">{message}</p>
      </div>
      <button onClick={onClose} aria-label="Close notification" className="text-gray-500 hover:text-gray-700 transition">
        <X className="w-4 h-4" />
      </button>
    </div>
  );
}

import { useState, useEffect } from "react";
import { ChatNotification } from "./ChatNotification";
import { AnimatePresence, motion } from "framer-motion";
import { registerNotifier } from "./NotificationManager"; // ðŸ‘ˆ import it

export interface Notification {
  id: string;
  title: string;
  message: string;
  type?: "success" | "error" | "info" | "warning";
  duration?: number;
  onClose?: () => void;
}

const MAX_NOTIFICATIONS = 3;

export function NotificationContainer() {
  const [notifications, setNotifications] = useState<Notification[]>([]);

  const addNotification = (notification: Omit<Notification, "id">) => {
    const id = crypto.randomUUID();
    const fullNotification: Notification = { id, ...notification };

    setNotifications((prev) => {
      const updated = [...prev, fullNotification];
      if (updated.length > MAX_NOTIFICATIONS) updated.shift();
      return updated;
    });

    if (notification.duration) {
      setTimeout(() => removeNotification(id), notification.duration);
    }
  };

  const removeNotification = (id: string) => {
    setNotifications((prev) => {
      const toRemove = prev.find((n) => n.id === id);
      if (toRemove?.onClose) toRemove.onClose();
      return prev.filter((n) => n.id !== id);
    });
  };

  useEffect(() => {
    registerNotifier(addNotification); // ðŸ‘ˆ this sets global notify
  }, []);

  return (
    <div className="fixed z-50 top-4 right-4 flex flex-col gap-3 w-fit max-w-sm dark:text-white">
      <AnimatePresence>
        {notifications.map((notification) => (
          <motion.div
            key={notification.id}
            layout
            initial={{ opacity: 0, scale: 0.95, y: 20 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.9, y: 20, transition: { duration: 0.3 } }}
            transition={{ type: "spring", stiffness: 300, damping: 20 }}
          >
            <ChatNotification
              {...notification}
              onClose={() => removeNotification(notification.id)}
            />
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  );
}

import { Notification } from "./NotificationContainer";
// Default fallback if container not mounted
let notifyFn: (notification: Omit<Notification, "id">) => void = (n) =>
  console.warn("Notification system is not mounted!", n);

export const notify = (notification: Omit<Notification, "id">) => {
  notifyFn(notification);
};

// Called by NotificationContainer to register itself
export const registerNotifier = (
  fn: (notification: Omit<Notification, "id">) => void
) => {
  notifyFn = fn;
};
